mapper.insert:
insert into aware_punish (levels, stu_id, times, description, status ,classroom)
values (#{levels,jdbcType=VARCHAR}, #{student.stuId,jdbcType=VARCHAR},
  #{times,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR},
  #{status,jdbcType=INTEGER},#{classroom,jdbcType=VARCHAR}
  )

mapper.select:
select * from aware_punish

    1=1

        and stu_id=#{stuId}


        and levels=#{levels}


        and classroom=#{classroom}
